package ir.artapps.gamebrowser.ui.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.lifecycle.Observer
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.snackbar.Snackbar
import ir.artapps.gamebrowser.R
import ir.artapps.gamebrowser.entities.Game
import ir.artapps.gamebrowser.entities.Rate
import ir.artapps.gamebrowser.ui.detail.DetailFragment
import kotlinx.android.synthetic.main.main_fragment.*
import org.koin.android.viewmodel.ext.android.viewModel

class HomeFragment private constructor() : Fragment(), HomeRecyclerViewAdapter.OnItemClickListener {

    private lateinit var mAdapter: HomeRecyclerViewAdapter
    private val viewModel: HomeViewModel by viewModel()

    companion object {
        fun newInstance() = HomeFragment()
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        val view = inflater.inflate(R.layout.main_fragment, container, false)
        setHasOptionsMenu(true)
        return view
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        mAdapter = HomeRecyclerViewAdapter()
        mAdapter.itemClickListener = this

        viewModel.apply {
            getGames(true)

            // observe updates of games
            gamesLiveData.observe(viewLifecycleOwner,
                Observer {

                    val g1 = Game().apply {
                        name = "airplane"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "data:image/jpeg;base64,"
                        banner =
                            "data:image/jpeg;base64,"
                        downloadLink = "http://hexgl.bkcore.com/play/"
                        isLocal = true
                    }

                    val g2 = Game().apply {
                        name = "tower"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "https://i.pinimg.com/736x/df/9d/83/df9d83b9c0b2a3bf3208cad4eaea56ca.jpg"
                        banner =
                            "https://i.pinimg.com/736x/df/9d/83/df9d83b9c0b2a3bf3208cad4eaea56ca.jpg"
                        downloadLink = "http://fe.bmqb.com/tower_game/index.html"
                        isLocal = true
                    }

                    val g3 = Game().apply {
                        name = "simon"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "data:image/jpeg;base64,"
                        banner =
                            "data:image/jpeg;base64,"
                        downloadLink = "https://weslleyaraujo.github.io/react-simon-says/#/board/"
                        isLocal = true
                    }

                    val g4 = Game().apply {
                        name = "gucci"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "https://assets.awwwards.com/awards/submissions/2020/01/5e1c968b0b749921639284.jpg"
                        banner = preview
                        downloadLink = "https://guccigrip.gucci.com/"
                        isLocal = true
                    }

                    val g5 = Game().apply {
                        name = "Seeds of dreams"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "https://assets.awwwards.com/awards/submissions/2019/11/5dcc0d9bf3b68110049925.jpg"
                        banner = preview
                        downloadLink = "https://gb.loccitane-seeds-of-dreams.com/en/"
                        isLocal = true
                    }

                    val g6 = Game().apply {
                        name = "gucci"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "https://assets.awwwards.com/awards/external/2020/02/5e44248cc1abf644519511.mp4"
                        banner = preview
                        downloadLink = "https://ispy.heihei.resn.co/"
                        isLocal = true
                    }

                    val g7 = Game().apply {
                        name = "snoopdogs"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "https://assets.awwwards.com/awards/submissions/2019/01/5c3e0a6444c29.png"
                        banner = preview
                        downloadLink = "https://snoopdogs.dogstudio.co/"
                    }

                    val g8 = Game().apply {
                        name = "noiz"
                        rate = Rate(4.7f, 10, null)
                        preview =
                            "https://assets.awwwards.com/awards/submissions/2019/11/5dc4f5905c614053664541.png"
                        banner = preview
                        downloadLink = "https://soundboard.heihei.resn.co/"
                        isLocal = true
                    }

                    val list: ArrayList<Game> = ArrayList()
                    list.add(g1)
                    list.add(g2)
                    list.add(g3)
                    list.add(g4)
                    list.add(g5)
                    list.add(g6)
                    list.add(g7)
                    list.add(g8)
                    list.addAll(it)

                    mAdapter.setGames(list)
                    refreshLayout.isRefreshing = false
                    mAdapter.loading = false
                    loading = false
                })

            // observe and show errors
            errorLiveData.observe(viewLifecycleOwner, Observer {
                loading = false
                mAdapter.loading = false
                refreshLayout.isRefreshing = false
                Snackbar.make(view, it, Snackbar.LENGTH_SHORT).show()
            })
        }

        recyclerView?.apply {
            val linearLayoutManager = GridLayoutManager(context, 2)
            linearLayoutManager.spanSizeLookup = object : GridLayoutManager.SpanSizeLookup() {
                override fun getSpanSize(position: Int): Int {
                    when (position) {
                        0, 7, 18 -> return 2
                        else -> return 1
                    }
                }
            }

            layoutManager = linearLayoutManager
            adapter = mAdapter
            addOnScrollListener(object : RecyclerView.OnScrollListener() {
                override fun onScrolled(recyclerView: RecyclerView, dx: Int, dy: Int) {
                    super.onScrolled(recyclerView, dx, dy)
                    linearLayoutManager.apply {

                        // check if recycler view moves to end and send request for next page
                        if (childCount + findFirstVisibleItemPosition() >= itemCount
                            && !viewModel.loading
                            && !viewModel.lastPage && dy > 0
                        ) {
                            viewModel.getGames(false)
                            viewModel.loading = true
                            mAdapter.loading = true
                        }
                    }
                }
            })
        }

        // pull to refresh
        refreshLayout.setOnRefreshListener {
            viewModel.getGames(true)
        }
    }

    // handle game item clicks and show detail fragment
    override fun onItemClick(view: View?, position: Int) {

        val item  = mAdapter.items[position]
        val detailFragment =
            DetailFragment.newInstance(item)
        detailFragment.show(childFragmentManager, position.toString())
    }
}
